pragma solidity 0.5.0; // FIXME update?

import '@thundercore/referral-solidity/contracts/Referral.sol';
import "openzeppelin-solidity/contracts/ownership/Ownable.sol";
import "openzeppelin-solidity/contracts/token/ERC20/ERC20.sol";

contract ERC677 is ERC20 {
    event Transfer(address indexed from, address indexed to, uint value, bytes data);
    function transferAndCall(address, uint, bytes calldata) external returns (bool);
}

contract IBurnableMintableERC677Token is ERC677 {
    function mint(address, uint256) public returns (bool);
    function burn(uint256 _value) public;
    function claimTokens(address _token, address _to) public;
}

contract ERC677Receiver {
    function onTokenTransfer(address _from, uint _value, bytes calldata _data) external returns(bool);
}

// To highlight the integration of TT-DAI and TT-USDT, as well as ERC677
// functionality, we are using the `IBurnableMintableERC677Token` interface
// despite not really needing the additional functions it describes.
contract DoubleOrNothing is Ownable, Referral, ERC677Receiver {
    IBurnableMintableERC677Token internal token;
    // This number shouldn't be considered the source of truth for the balance
    // of whatever contract this token is currently pointing to. It is primarily
    // a counter for onTokenTransfer calls, validating that the stated number of
    // tokens was actually transferred.
    uint256 internal tokenBalance;

    event BetSettled(address player, uint256 winnings);

    constructor(
        uint _decimals,
        uint _referralBonus,
        uint _secondsUntilInactive,
        bool _onlyRewardActiveReferrers,
        uint256[] memory _levelRate,
        uint256[] memory _refereeBonusRateMap,
        address _tokenContractAddress
    ) Referral(
        _decimals,
        _referralBonus,
        _secondsUntilInactive,
        _onlyRewardActiveReferrers,
        _levelRate,
        _refereeBonusRateMap
    ) public {
        // TODO use ERC 165 to validate interface?
        token = IBurnableMintableERC677Token(_tokenContractAddress);
        tokenBalance = token.balanceOf(address(this));
    }

    // TODO stablecoin referral support
    function bet(address payable _referrer) payable external {
        if(!hasReferrer(msg.sender)) {
            addReferrer(_referrer);
        }
        bet();
    }

    // TODO stablecoin referral support
    // This function handles ERC677 token bets.
    // It is internal because it trusts that the tokens received were
    // actually sent (as opposed to someone calling the function arbitrarily
    // without having actually sent tokens).
    function bet(uint256 _amount) internal {
        // The smart contract needs to be able to pay double the value sent.
        require(_amount * 2 <= token.balanceOf(address(this)), 'Contract balance too low!');

        // Transferring tokens from the user to the smart contract first.
        // This prevents reentrancy attacks on the contract.
        // This is commented out since token transfers should only happen
        // via onTokenTransfer, which validates the transfer.
        //require(
        //    token.transferFrom(msg.sender, address(this), _amount),
        //    'Sender does not have enough funds or has not approved this contract for bet amount!'
        //);

        uint256 winnings = 0;

        // DO NOT USE THIS IN PRODUCTION, IT IS INSECURE
        if(uint256(blockhash(block.number -1)) % 2 == 0) {
          // 3% is deducted to cover the referral bonus
          winnings = msg.value * 197/100;
          token.transfer(msg.sender, winnings);
        }

        // payReferral(msg.value);
        emit BetSettled(msg.sender, winnings);
    }

    // value transfer tx based bet.
    function bet() payable public {
        // msg.value is added to the balance to begin with so you need to double it
        require(msg.value * 2 <= address(this).balance, 'Balance too low!');
        uint256 winnings = 0;

        // DO NOT USE THIS IN PRODUCTION, IT IS INSECURE
        if(uint256(blockhash(block.number -1)) % 2 == 0) {
            // 3% is deducted to cover the referral bonus
            winnings = msg.value * 197/100;
            address(msg.sender).transfer(winnings);
        }

        payReferral(msg.value);
        emit BetSettled(msg.sender, winnings);
    }

    // This verifies that the difference in balance between checks is _at least_
    // as much as the stated amount of tokens sent. Tokens could have been
    // transferred directly to the contract without this being called.
    // This would mean that (updatedBalance - tokenBalance) would be greater than
    // _value, so the contract might have extra tokens.
    // With no data sent (0x0), the balance is simply updated.
    // Any data provided indicates that a user wants to make a bet.
    function onTokenTransfer(address /*_from*/, uint _value, bytes calldata _data) external returns(bool) {
        if (_data.length == 0) {
            tokenBalance = token.balanceOf(address(this));
        } else {
            uint256 updatedBalance = token.balanceOf(address(this));
            require(updatedBalance - tokenBalance >= _value, "onTokenTransfer was called but the tokens were not sent.");
            tokenBalance = updatedBalance;
            bet(_value);
        }
        return true;
    }

    function withdraw(uint256 _amount) external onlyOwner {
        require(_amount <= address(this).balance, 'Balance too low!');
        // explicit cast
        address payable owner = address(uint160(owner()));
        owner.transfer(_amount);
    }

    function withdrawTokens(uint256 _amount) external onlyOwner {
        require(_amount <= token.balanceOf(address(this)), 'Token balance too low!');
        token.transfer(owner(), _amount);
    }

    function updateTokenAddress(address _token) external onlyOwner {
        tokenBalance = 0; // Reentrancy guard
        token = IBurnableMintableERC677Token(_token);
        tokenBalance = token.balanceOf(address(this));
    }

    // This fallback function eats all funds and gas sent to it. The owner can
    // withdraw from the contract balance via the withdraw function above.
    function() external payable {}
}
